import { FormControl, FormControlLabel, Radio, RadioGroup, TextField } from "@mui/material";

import { Controller, useFormContext } from "react-hook-form";
import { getFieldState } from "../utils/getFieldState";
import InputMask from "react-input-mask";

export const OrganizationInfo = () => {
  const { control } = useFormContext();
  return (
    <>
      <h3>Данные об организации</h3>

      <FormControl fullWidth sx={{ mb: 2 }}>
        <Controller
          name="status"
          control={control}
          rules={{
            required: "Поле обязательное",
          }}
          render={({ field, fieldState, formState }) => (
            <tr>
              <td>* Организационно-правовая форма:</td>
              <td>
                <div style={{ display: "flex" }}>
                  <RadioGroup
                    {...field}
                    {...getFieldState({ fieldState, formState })}
                    row
                    aria-labelledby="demo-row-radio-buttons-group-label"
                    name="row-radio-buttons-group">
                    <FormControlLabel value="АО" control={<Radio />} label="АО" />
                    <FormControlLabel value="ТОО" control={<Radio />} label="ТОО" />
                    <FormControlLabel value="ИП" control={<Radio />} label="ИП" />
                  </RadioGroup>
                </div>
              </td>
            </tr>
          )}
        />
      </FormControl>

      <FormControl fullWidth sx={{ mb: 2 }}>
        <Controller
          name="iin"
          control={control}
          rules={{
            required: "Поле обязательное",
            validate: (value) => {
              if (value) {
                return true;
              } else {
                return "Not Valid";
              }
            },
          }}
          render={({ field, fieldState, formState }) => (
            <tr>
              <td> * БИН /ИИН:</td>
              <td>
                <InputMask
                  mask="9999-9999-9999"
                  id="outlined-basic"
                  {...getFieldState({ fieldState, formState })}
                  {...field}
                />
              </td>
            </tr>
          )}
        />
      </FormControl>

      <FormControl fullWidth sx={{ mb: 2 }}>
        <Controller
          name="fullNameOfOrganization"
          control={control}
          rules={{
            required: "Поле обязательное",
            validate: (value) => {
              if (value.length < 200) {
                return true;
              } else {
                return "Not Valid";
              }
            },
          }}
          render={({ field, fieldState, formState }) => (
            <tr>
              <td> * Полное наименование:</td>
              <td>
                <TextField
                  id="outlined-basic"
                  label="Name"
                  {...getFieldState({ fieldState, formState })}
                  variant="outlined"
                  {...field}
                />
              </td>
            </tr>
          )}
        />
      </FormControl>
    </>
  );
};
