import styled from "@emotion/styled";
import { FC } from "react";
import { CommentsProps } from "./Table";
import { Box as MUIBox } from "@mui/system";
import { Icon, IconMed, IconSmall } from "./IconComponent/Icon";
import moment from "moment";

const Box = styled(MUIBox)`
  flex-direction: row;
  justify-content: space-between;
  align-items: center;
  /* padding: 16px 24px; */
  gap: 36px;
  top: 50%;
  left: 50%;
  width: 600px;
  position: absolute;
  background: #ffffff;
  box-shadow: inset 0px -1px 0px #f0f0f0;
  flex: none;
  order: 0;
  align-self: stretch;
  flex-grow: 0;

  transform: translate(-50%, -50%);

  background-color: white;
  border-radius: 2px;
  box-shadow: "0px 3px 6px -4px rgba(0, 0, 0, 0.12), 0px 6px 16px rgba(0, 0, 0, 0.08), 0px 9px 28px 8px rgba(0, 0, 0, 0.05)";
  p: 4;
`;

export const ModalInfo = ({ com }: any) => {
  const getFormatedDate = (date: number) => {
    moment.lang("ru");
    const formatedDate = moment(date * 1000).format("DD MMMM YYYY");

    return [formatedDate];
  };
  return (
    <>
      <Box>
        <h3
          style={{
            height: "24px",
            boxShadow: "inset 0px -1px 0px #F0F0F0",

            padding: " 16px 24px",
          }}>
          Comments
        </h3>

        {com.map((i: any) => (
          <>
            <div style={{ display: "flex", alignItems: "center" }}>
              <IconSmall type="Dot" />
              <div
                style={{
                  width: "24px",
                  height: "24px",
                  left: "0",
                  top: "0",
                  backgroundColor: "rgba(0, 40, 119, 0.45)",
                  borderRadius: "15px",
                  textAlign: "center",
                  alignItems: "center",
                  margin: "0 8px",
                }}>
                <IconMed type="Profile" />
              </div>
              <div>
                {i.name}
                <span style={{ color: "rgba(0, 40, 119, 0.45)", marginLeft: "8px" }}>
                  {getFormatedDate(i.date)}
                </span>
              </div>
            </div>
            <p style={{ lineHeight: "22px" }}>{i.comment}</p>
          </>
        ))}
        <button>close</button>
      </Box>
    </>
  );
};
