import { Button, Checkbox, FormControl, TextField } from "@mui/material";
import grey from "@mui/material/colors/grey";
import { pink } from "@mui/material/colors";
import RadioButtonUncheckedIcon from "@mui/icons-material/RadioButtonUnchecked";
import CheckCircleIcon from "@mui/icons-material/CheckCircle";
import { useCallback, useState } from "react";
import { Controller, useForm } from "react-hook-form";
import { getFieldState } from "../utils/getFieldState";
import { validateIIN } from "../utils/validateNumbers";

export const FormPage = () => {
  const label = { inputProps: { "aria-label": "Checkbox demo" } };
  // const [iin, setIin] = useState("");
  // const [fullName, setFullName] = useState("");
  const { handleSubmit, control, reset } = useForm({
    mode: "onChange",
    defaultValues: {
      iin: "",
      fullName: "",
    },
  });

  // const validateForm = (e: any) => {
  //   e.preventDefault();

  //   if (iin.length < 12) {
  //     alert("Enter right IIN");
  //     return;
  //   }

  //   const data = {
  //     iin,
  //   };
  // };
  const onSubmit = useCallback(
    (values: any) => {
      alert("SUBMIT");
      console.log(values);

      reset();
    },
    [reset],
  );
  return (
    <form onSubmit={handleSubmit(onSubmit)}>

      <h3>Данные об организации</h3>
      <FormControl fullWidth sx={{ mb: 2 }}>
        <Controller
          name="iin"
          control={control}
          rules={{
            required: "Поле обязательное",
            validate: (value) => {
              if (validateIIN(value)) {
                return true;
              } else {
                return "Not Valid";
              }
            },
          }}
          render={({ field, fieldState, formState }) => (
            <div style={{ display: "flex" }}>
              <p>БИН/ИИН:</p>
              <TextField
                type="number"
                id="outlined-basic"
                label="IIN"
                {...getFieldState({ fieldState, formState })}
                variant="outlined"
                {...field}
              />
            </div>
          )}
        />
      </FormControl>
      <FormControl fullWidth sx={{ mb: 2 }}>
        <Controller
          name="fullName"
          control={control}
          rules={{
            required: "Поле обязательное",
            validate: (value) => {
              if (value.length < 200) {
                return true;
              } else {
                return "Not Valid";
              }
            },
          }}
          render={({ field, fieldState, formState }) => (
            <div style={{ display: "flex" }}>
              <p>Полное наименование:</p>
              <TextField
                id="outlined-basic"
                label="Name"
                {...getFieldState({ fieldState, formState })}
                variant="outlined"
                {...field}
              />
            </div>
          )}
        />
      </FormControl>
      <h3>Данные представителя</h3>
      <Button type="submit" variant="contained">
        Submit
      </Button>
    </form>
  );
};
