import { FormControl, TextField } from "@mui/material";
import { FC } from "react";
import { Controller } from "react-hook-form";
import { getFieldState } from "../utils/getFieldState";

type BankProps = {
  iik: any;
  bik: any;
  kbe: any;
  control: any;
};
export const Bank: FC<BankProps> = ({ iik, bik, kbe, control }) => {
  console.log("iik", iik);
  return (
    <FormControl fullWidth sx={{ mb: 2 }}>
      <Controller
        name="iik"
        control={control}
        rules={{
          required: "Поле обязательное",
          validate: (value) => {
            if (value.length > 3) {
              return true;
            } else {
              return "Not Valid";
            }
          },
        }}
        render={({ field, fieldState, formState }) => (
          <tr>
            <td>ИИК:</td>
            <td>
              <TextField
                id="outlined-basic"
                label=""
                placeholder="Введите"
                {...getFieldState({ fieldState, formState })}
                variant="outlined"
                {...field}
              />
            </td>
          </tr>
        )}
      />
    </FormControl>
  );
};
